mapper.select:
select * from () tmp

    and company_name = #{factoryName}
    and group_name = #{groupName}
    and lower(eq_id) like concat('%', lower(#{eqId}),'%') 
    and lower(mc_id) like concat('%', lower(#{mcId}),'%') 
    and sim_id like concat('%',#{simId},'%') 
    and lower(prod_type) like concat('%', lower(#{prodType}),'%') 

        and status = #{statusCode}


order by update_time desc, status desc, company_name, group_name, mc_id
