mapper.select:
SELECT CASE WHEN company_name IS NULL THEN '总计' ELSE company_name END AS company_name, CASE WHEN group_name IS NULL THEN '小计' ELSE group_name END AS group_name, chk_cnt, ok_cnt, err_cnt, no_tpl_cnt, lack_params_cnt, unsuitable_cnt, overflow_cnt, comp_err_cnt, offline_tpl_cnt, lack_tpl_detail_cnt, err_ratio FROM ( SELECT company_name, group_name, chk_cnt, ok_cnt, err_cnt, no_tpl_cnt, lack_params_cnt, unsuitable_cnt, overflow_cnt, comp_err_cnt, offline_tpl_cnt, lack_tpl_detail_cnt, err_cnt / chk_cnt as err_ratio FROM ( SELECT company_name, group_name, count(code) as chk_cnt, sum(case when code = 0 then 1 else 0 end) as ok_cnt, sum(case when code > 0 then 1 else 0 end) as err_cnt, sum(case when code = 1 then 1 else 0 end) as no_tpl_cnt, sum(case when code = 2 then 1 else 0 end) as lack_params_cnt, sum(case when code = 3 then 1 else 0 end) as unsuitable_cnt, sum(case when code = 4 then 1 else 0 end) as overflow_cnt, sum(case when code = 5 then 1 else 0 end) as comp_err_cnt, sum(case when code = 6 then 1 else 0 end) as offline_tpl_cnt, sum(case when code = 7 then 1 else 0 end) as lack_tpl_detail_cnt FROM ( SELECT company_name, group_name, dep_name, machine_no, device_m_id, pbox_id, prod_type, chk_dt, code, description FROM ( SELECT sim_id, chk_dt, prod_type, code, description FROM qtech_eq_dwd.im_aa_list_chk_result_detail
and chk_dt between #{params.beginTime} and #{params.endTime}
) a INNER JOIN ( SELECT a.device_id, b.company_name, b.dep_name, b.group_name, a.device_m_id, devicetype, a.devicename, devicenetip, c.pbox_id, machine_no FROM qtech_eq_ods.ems_t_device a INNER JOIN qtech_eq_ods.ems_t_pbox_info c ON c.dev_id = CAST(a.device_id AS DOUBLE) LEFT JOIN qtech_eq_ods.ems_v_code_name b ON a.hierarchy_code = b.hierarchy_show_code LEFT JOIN qtech_eq_ods.ems_t_collector_program d ON c.collector_program_id = d.id ) b ON a.sim_id = b.pbox_id ) ta
GROUP BY ROLLUP(company_name, group_name)
GROUP BY ROLLUP(company_name, group_name)
AND company_name = #{factoryName} AND group_name = #{groupName}
) tb ) tc ORDER BY CASE company_name WHEN '古城厂区' THEN 1 WHEN '台虹厂区' THEN 2 WHEN '汉浦厂区' THEN 3 WHEN 'QT_India' THEN 4 WHEN '总计' THEN 6 ELSE 5 END ASC, CASE WHEN group_name LIKE '%COB一%' THEN 1 WHEN group_name LIKE '%COB二%' THEN 2 WHEN group_name LIKE '%COB三%' THEN 3 WHEN group_name LIKE '%COB四%' THEN 4 WHEN group_name LIKE '%COB五%' THEN 5 WHEN group_name LIKE '%COB六%' THEN 6 WHEN group_name LIKE '%COB七%' THEN 7 WHEN group_name LIKE '%COB八%' THEN 8 WHEN group_name LIKE '%COB九%' THEN 9 WHEN group_name LIKE '%COB十%' THEN 10 WHEN group_name LIKE '%光学一%' THEN 11 WHEN group_name LIKE '%光学二%' THEN 12 WHEN group_name LIKE '%光学三%' THEN 13 WHEN group_name LIKE '%光学四%' THEN 14 WHEN group_name LIKE '%试产一%' THEN 15 WHEN group_name LIKE '%试产二%' THEN 16 WHEN group_name LIKE '%试产三%' THEN 17 WHEN group_name LIKE '小计' THEN 19 ELSE 18 END ASC
