mapper.select:
select distinct company_name from qtech_eq_ods.ems_t_device a inner join qtech_eq_ods.ems_t_pbox_info c on c.dev_id = a.device_id inner join qtech_eq_ods.ems_v_code_name b on a.hierarchy_code = b.hierarchy_show_code inner join ( select distinct sim_id from qtech_eq_dwd.im_aa_list_chk_result_detail
and chk_dt between #{params.beginTime} and #{params.endTime}
) d on c.pbox_id = d.sim_id order by case when company_name like '%台虹%' then 1 when company_name like '%古城%' then 2 when company_name like '%India%' then 3 else 4 end

select distinct factory_name as company_name
from qtech_biz_2.dwd_wb_wire_usage_detail


        and create_date between #{params.beginTime} and #{params.endTime}


order by company_name asc;

select distinct company_name from qtech_eq_ods.ems_t_device a inner join qtech_eq_ods.ems_t_pbox_info c on c.dev_id = a.device_id inner join qtech_eq_ods.ems_v_code_name b on a.hierarchy_code = b.hierarchy_show_code inner join (select distinct sim_id as sim_id from qtech_eq_dwd.im_wb_olp_chk_result_detail
and dt between #{params.beginTime} and #{params.endTime}
and dt > date_sub(now(), interval 1 day)
) d on c.pbox_id = d.sim_id
