mapper.select:
with ta as ( select device_id, create_date, remote_control from ( select device_id, create_date, remote_control, row_number() over(partition by device_id order by create_date desc) rank from QT_MODEL_DDC.ALL_DEVICE_REMOTE where create_date > minutes_sub(now(), 5) and remote_control REGEXP '^-?[0-9]+(\.[0-9]+)?$') a where rank = 1),
tb as ( select b.company_name, b.dep_name, b.group_name, a.device_m_id, a.devicetype, a.machine_no, c.pbox_id, e.remote_control from QT_MODEL_EMS.T_DEVICE_CALCGD1JH1U82GWWIONK a left join QT_MODEL_EMS.V_CODE_NAME b on a.hierarchy_code = b.hierarchy_show_code left join QT_MODEL_EMS.T_PBOX_INFO c on c.dev_id = a.device_id left join QT_MODEL_EMS.T_COLLECTOR_PROGRAM d on c.collector_program_id = d.id left join ta e on a.device_id = e.device_id where dep_name like '%COB%' and a.devicetype in ('AA', 'WB', 'DB', 'HM')),
tc as ( select distinct eqid from QT_MODEL_MES.T_PARAMETER_RESULT
and ask_date > minutes_sub(now(), 5) and device_type in ('AA', 'WB', 'DB', 'HM'))
select company_name, dep_name, group_name, device_m_id, devicetype, machine_no, pbox_id, remote_control, status from ( select company_name, dep_name, group_name, device_m_id, devicetype, machine_no, pbox_id, remote_control, case when (devicetype in ('AA', 'DB', 'HM') and remote_control != '2') or (devicetype = 'WB' and remote_control != '5') then 1 else 2 end status from tb
dep_name like '%COB%' and devicetype in ('AA', 'WB', 'DB', 'HM') and remote_control is not null and device_m_id in (select eqid from tc) and company_name = #{factoryName} and group_name = #{groupName} and devicetype = #{deviceType} and device_m_id = #{eqId} and machine_no = #{mcId} and pbox_id = #{simId}
) a
and status = #{status}
