mapper.select:


    and company_name = #{vo.factoryName,jdbcType=VARCHAR}
    and group_name = #{vo.groupName,jdbcType=VARCHAR}
    and device_m_id = #{vo.eqId,jdbcType=VARCHAR}
    and machine_no = #{vo.mcId,jdbcType=VARCHAR}
    and prod_type like concat('%',#{vo.prodType,jdbcType=VARCHAR},'%')
    and pbox_id = #{vo.simId,jdbcType=VARCHAR}
    and code = #{vo.status,jdbcType=INTEGER}

order by dt desc
