mapper.select:
select factory_name, workshop, prod_type, actual_wire_usage, standard_wire_usage, wire_width, tf.qty as bom_wire_usage, percents, if(percents is null, 2, if(percents > 0, 1, 0)) as status, yield from (select factory_name, workshop, prod_type, actual_wire_usage, standard_wire_usage, wire_width, percents, if(percents is null, 2, if(percents > 0, 1, 0)) as status, yield from (select factory_name, workshop, prod_type, actual_wire_usage, standard_wire_usage, wire_width, round(actual_wire_usage / standard_wire_usage * 100 - 100, 2) as percents, yield from (select factory_name, workshop, prod_type, round(wire_dosage / yield / 1000000, 7) as actual_wire_usage, tb.wire_width, round(cast(tb.standard_wire_usage as float), 7) as standard_wire_usage, yield from (select factory_name, workshop, prod_type, sum(wire_usage) wire_dosage, sum(yield) yield from qtech_biz_2.dwd_wb_wire_usage_detail
and create_date between #{params.beginCreateDate} and #{params.endCreateDate}
and factory_name = #{factoryName} and workshop = #{workshop} and prod_type = #{prodType}
group by factory_name, workshop, prod_type) ta left join qtech_biz_2.ads_wirebond_standard_usage tb on ta.prod_type = tb.mc_id) tc) td) te left join qtech_biz_2.ads_aa_bom_info tf on te.prod_type = tf.part_spec
and wire_width = #{wireWidth} and status = #{status}
order by yield desc

mapper.update:
update workshop_level_wire_usage
workshop = #{workshop}, prod_type = #{prodType}, wire_size = #{wireWidth}, actual_wire_usage = #{actualWireUsage}, standard_wire_usage = #{standardWireUsage}, bom_wire_usage = #{bomWireUsage}, yield = #{yield}, percents = #{percents}, create_date = #{createDate}, status = #{status},
where factory_name = #{factoryName}
